function main_index(){var f=document.body.clientHeight;var g=document.querySelector("#descricao");var e=document.querySelector("#informacoes-existentes");if(g&&e){var d=document.querySelector(".container > header");var c=d.offsetHeight;var i=document.querySelector(".container > footer");var h=i.offsetHeight;var b=10;var a=f-c-h;e.style.height=a+b+"px";g.style.height=a+b+"px"}}main_index();function showElementsOfAClass(c){var d=document.querySelectorAll("."+c);for(var a=0;a<d.length;a++){var b=d[a];b.classList.remove("hidden")}}function hideElementsOfAClass(c){var d=document.querySelectorAll("."+c);for(var a=0;a<d.length;a++){var b=d[a];b.classList.add("hidden")}}function toggleAllAnswers(b){var d=["musico","fa"];for(var a in d){var c=d[a];if(c==b){showElementsOfAClass(c)}else{hideElementsOfAClass(c)}}}function showElements(b){document.querySelector("#questions").classList.remove("hidden");var a=this.value;toggleAllAnswers(a)}function main_pesquisa(){var a=document.querySelectorAll("input.answer_main");for(var c=0;c<a.length;c++){var b=a[c];b.addEventListener("click",showElements,false)}}main_pesquisa();var httpRequest;function makeRequestBand(a,c,d){if(window.XMLHttpRequest){httpRequest=new XMLHttpRequest()}else{if(window.ActiveXObject){try{httpRequest=new ActiveXObject("Msxml2.XMLHTTP")}catch(b){try{httpRequest=new ActiveXObject("Microsoft.XMLHTTP")}catch(b){}}}}if(!httpRequest){return false}if(d){httpRequest.onreadystatechange=d}httpRequest.open("POST","/band/"+a);httpRequest.setRequestHeader("Content-Type","application/x-www-form-urlencoded");httpRequest.send("band="+encodeURIComponent(c))}var votacaoInput=document.querySelector("#adicionar-item-votacao-text");function showNewBand(){if(httpRequest.readyState===4){if(httpRequest.status===200){var d=httpRequest.responseText.split("\n");var c=d[0];var b=d[1];var e=document.querySelector(".list_bands_user_likes");e.innerHTML+='<li><input type="checkbox" checked="checked" class="item-votacao" value="'+b+'" /> '+c+"</li>";votacaoInput.value="";var a=document.querySelectorAll(".item-votacao");addListenerMarcacao(a)}else{console.log("There was a problem with the request.")}}}function adicionarItem(){if(votacaoInput.value.trim()!=""){makeRequestBand("add/",votacaoInput.value,showNewBand)}}function enterPressed(a){if(a.keyCode==13){adicionarItem()}}function marcacaoItem(){if(this.checked){this.setAttribute("checked","checked");makeRequestBand("like/",this.value)}else{this.removeAttribute("checked");makeRequestBand("unlike/",this.value)}}function addListenerMarcacao(b){for(var a=0;a<b.length;++a){b[a].addEventListener("click",marcacaoItem,false)}}function votacao(){var b=document.querySelector("#adicionar-item-votacao-button");var a=document.querySelectorAll(".item-votacao");if(b){b.addEventListener("click",adicionarItem,false);votacaoInput.addEventListener("keypress",enterPressed,false);addListenerMarcacao(a)}}votacao();